'use strict'

const generic = require('@nodeguy/generic')
const is = require('@nodeguy/type').is

const assign = generic.function({name: 'assign', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-object.assign
assign.method([is(Object), is(Object)],
  (...objects) =>
    Object.assign({}, ...objects)
)

assign.method([is(Map), is(Map)],
  (map1, map2) =>
    new Map([...map1, ...map2])
)

module.exports = assign
