'use strict'

const generic = require('@nodeguy/generic')
const _ = generic._
const is = require('@nodeguy/type').is

const concat = generic.function({name: 'concat', curry: true})

// https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.concat
concat.method([_, is(String)],
  (arg, string) =>
    string.concat(arg)
)

// https://www.ecma-international.org/ecma-262/6.0/#sec-array.prototype.concat
concat.method([_, is(Array)],
  (arg, array) =>
    array.concat(arg)
)

module.exports = concat
