'use strict'

const assert = require('assert')
const G = require('../../lib')

describe('assign', function () {
  it('Object', function () {
    assert.deepEqual(G.assign({a: 1}, {b: 2}), {a: 1, b: 2})
  })

  it('Map', function () {
    assert.deepEqual(
      G.assign(new Map([['a', 1]]), new Map([['b', 2]])),
      new Map([['a', 1], ['b', 2]])
    )
  })
})
